// シンプルなAPI統合例
try {
  const response = await fetch('https://api.homedesignpro.ai/v1/transform', {
    method: 'POST',
    headers: {
      'Authorization': 'Bearer YOUR_API_KEY', // 実際のキーに置き換えてください
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      image_url: 'https://...', // 実際のURL
      style: 'modern',
      prompt: 'ラグジュアリーアパートメント風に変換'
    })
  });

  if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

  const result = await response.json();

  if (result.staged_image_url) {
    console.log('変換後の画像:', result.staged_image_url);
  } else {
    console.error('staged_image_url がレスポンスにありません:', result);
  }

} catch (error) {
  console.error('APIリクエスト失敗:', error);
}
