// 간단한 API 연동 예시
try {
  const response = await fetch('https://api.homedesignpro.ai/v1/transform', {
    method: 'POST',
    headers: {
      'Authorization': 'Bearer YOUR_API_KEY', // 실제 키로 변경
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      image_url: 'https://...', // 실제 이미지 URL
      style: 'modern',
      prompt: '럭셔리 아파트 스타일로 변환'
    })
  });

  if (!response.ok) throw new Error(`HTTP error: ${response.status}`);

  const result = await response.json();

  if (result.staged_image_url) {
    console.log('변환된 이미지:', result.staged_image_url);
  } else {
    console.error('staged_image_url 없음:', result);
  }

} catch (error) {
  console.error('API 요청 실패:', error);
}
